children.listData:
{{sel_subject.selectedOptionValue && fetchModuleContent.data.map(c => {
	return {
		id: c.id,
		type: c.content_type,
		name: c.title,
		markComplete: c.mark_complete,
		comment: c.comment
	}
}) 

}}

jsonPathKeys:
async () => {
  const modules = await fetchModules.run();
  return modules.map(m => {
    return {
      Id: m.id,
      Name: m.name,
      Duration: m.duration,
      Description: m.description,
      CourseId: m.course_id,
      SubjectId: m.course_subject_id
    };
  });
}
